import React, { useState } from "react";
import Sidebar from "./Sidebar";
import "./Sidebar.css"; // Ensure Sidebar.css is in the same folder
import Profile from "./Profile";
import "./Dashboard.css";

const Dashboard = () => {
  const [selectedFunctionality, setSelectedFunctionality] = useState(null);

  const functionalities = [
    "Data Upload",
    "Preview and Profiling",
    "Redundancy and Consistency Cleaning",
    "Outlier Detection",
    "Data Standardization",
    "Data Transformation",
    "Collaborative Cleaning",
    "Export Options",
    Prof
  ];

  const handleFunctionalityClick = (name) => {
    setSelectedFunctionality(name);
  };

  return (
    <div className="dashboard-container">
      <Sidebar
        functionalities={functionalities}
        onFunctionalityClick={handleFunctionalityClick}
      />
      <div className="main-content">
        {selectedFunctionality ? (
          <h2>{selectedFunctionality}</h2>
        ) : (<h1> </h1>
        )}
      </div>
      <Profile />
    </div>
  );
};

export default Dashboard;
