import React from "react";

export const Header = (props) => {
  const headerStyle = {
    backgroundColor: "rgba(211, 122, 122, 0)", // Fully transparent background
    color: "#000", // Black text
    textAlign: "center",
    padding: "20px 0",
    position: "fixed",
    top: 0,
    width: "100%",
    fontFamily: "Arial, sans-serif",
    fontSize: "18px",
    borderBottom: "1px solid rgba(221, 221, 221, 0.2)", // Subtle transparent border
    backdropFilter: "blur(10px)", // Adds a frosted glass effect
    zIndex: 1000, // Ensures it stays on top of other content
  };

  const titleStyle = {
    fontWeight: "bold",
    margin: "0",
    padding: "10px 0",
  };

  const paragraphStyle = {
    margin: "10px 0",
    fontSize: "16px",
  };

  return (
    <header id="header" style={headerStyle}>
      <div className="intro">
        <div className="overlay">
          <div className="container">
            <div className="row">
              <div className="col-md-8 col-md-offset-2 intro-text">
                <h1 style={titleStyle}>
                  {props.data ? props.data.title : ""}
                  <span></span>
                </h1>
                <p style={paragraphStyle}>
                  {props.data ? props.data.paragraph : ""}
                </p>
                {/* <a
                  href="#features"
                  className="btn btn-custom btn-lg page-scroll"
                >
                  Learn More
                </a>{" "} */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};
