import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // React Router v6 components
import { Navigation } from "./components/navigation";
import { Header } from "./components/header";
import About from "./components/about";
import Footer from "./components/footer";
import Login from "./components/Login";
import Sidebar from "./components/Sidebar";
import Signup from "./components/Signup";
import Dashboard from "./components/Dashboard";
import DataUpload from "./components/DataUpload";
impo
import SmoothScroll from "smooth-scroll";
import "./App.css";
import Contact from "./components/Contact";
// Initialize smooth scrolling for navigation links
export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
});

const App = () => {
  const [landingPageData] = useState({});
  // const [showFloatingContent, setShowFloatingContent] = useState(true);

  useEffect(() => {
    // Set landing page data if required
    // setLandingPageData(JsonData);
  }, []);

  return (
    <Router>
      <Navigation />
      <Routes>
        {/* Route for Home */}
        <Route path="/" element={<Header data={landingPageData.Header} />} />

        {/* Route for About */}
        <Route path="/about" element={<About />} />

        {/* Route for Login */}
        <Route path="/login" element={<Login />} />

        {/* Route for Signup */}
        <Route path="/signup" element={<Signup />} />

        {/* Route for Dashboard */}
        <Route path="/dashboard" element={<Dashboard />} />

        {/* Optional Sidebar for Dashboard */}
        <Route path="/sidebar" element={<Sidebar />} />

        {/* Route for Contact */}
        <Route path="/contact" element={<Contact />} />
        {/* Route for Data Upload */}
        <Route path="/data-upload" element={<DataUpload />} />
      </Routes>

      {/* Floating Content */}

      {/* <div className="floating-content">
  <p>Welcome to our website! Enjoy browsing.</p>
  <button className="close-btn" onClick={() => setShowFloatingContent(false)}>X</button>
</div> */}

      <Footer />
    </Router>
  );
};

export default App;
