import React, { useState } from "react";
import "./Sidebar.css"; // Import the CSS file for styling

const Sidebar = ({ functionalities, onFunctionalityClick }) => {
  const [collapsed, setCollapsed] = useState(false);
  const pathsWithoutNav = ["/", "/sidebar", "/dataupload", "/previewandprofiling",
    "/redundancy-cleaning", "/collaborative-cleaning", "/data-transformation", 
    "/export-options", "/outlier-detection", "/profile"];
  // Toggle collapse state
  const toggleSidebar = () => {
    setCollapsed(!collapsed);
  };

  return (
    <div><br/><br/><br/><br/>
      <div className={`sidebar ${collapsed ? "collapsed" : ""}`}>
        <button className="collapse-btn" onClick={toggleSidebar}>
          {collapsed ? ">" : "<"} {/* Icon indicating collapse/expand */}
        </button>
        <ul>
          {functionalities.map((func, index) => (
            <li key={index} onClick={() => onFunctionalityClick(func)}>
              {func}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
