import React from "react";
import "./Sidebar.css";

const Sidebar = ({ functionalities, onFunctionalityClick }) => {
  return (
    <div className="sidebar">
      <ul>
        {functionalities.map((func, index) => (
          <li key={index} onClick={() => onFunctionalityClick(func)}>
            {func}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
