import React from "react";

/**
 * Footer component for the landing page.
 * Displays a fixed footer with links, social media icons, and copyright information.
 *
 * @component
 * @example
 * return (
 *   <Footer />
 * )
 */
const Footer = () => {
  const footerStyle = {
    backgroundColor: "rgba(255, 255, 255, 0.8)", // White background with transparency
    color: "#000", // Black text
    textAlign: "center",
    padding: "20px",
    position: "fixed",
    bottom: 0,
    width: "100%",
    fontFamily: "Arial, sans-serif",
    fontSize: "14px",
    borderTop: "1px solid rgba(221, 221, 221, 0.8)", // Subtle border with transparency
    backdropFilter: "blur(10px)", // Adds a blur effect for better readability
    zIndex: 1000, // Ensures it stays on top of other content
  };

  const linkStyle = {
    color: "#007bff", // Blue for links
    textDecoration: "none",
    fontWeight: "bold",
    margin: "0 10px",
    transition: "color 0.3s ease",
  };

  const linkHoverStyle = {
    color: "#0056b3", // Darker blue on hover
  };

  const socialIconStyle = {
    margin: "0 8px",
    fontSize: "18px",
    cursor: "pointer",
    transition: "color 0.3s ease",
  };

  return (
    <footer style={footerStyle}>
      <p>
        &copy; {new Date().getFullYear()} Intellicleanse. All rights reserved.
      </p>
      <p>
        <a
          href="#about"
          style={linkStyle}
          onMouseEnter={(e) => (e.target.style.color = linkHoverStyle.color)}
          onMouseLeave={(e) => (e.target.style.color = linkStyle.color)}
        >
          About Us
        </a>
        |
        <a
          href="#contact"
          style={linkStyle}
          onMouseEnter={(e) => (e.target.style.color = linkHoverStyle.color)}
          onMouseLeave={(e) => (e.target.style.color = linkStyle.color)}
        >
          Contact
        </a>
        |
        <a
          href="#privacy"
          style={linkStyle}
          onMouseEnter={(e) => (e.target.style.color = linkHoverStyle.color)}
          onMouseLeave={(e) => (e.target.style.color = linkStyle.color)}
        >
          Privacy Policy
        </a>
      </p>
      <p>
        Follow us: 
        <i
          className="fab fa-facebook"
          style={socialIconStyle}
          onMouseEnter={(e) => (e.target.style.color = "#4267B2")}
          onMouseLeave={(e) => (e.target.style.color = "#000")}
        />
        <i
          className="fab fa-twitter"
          style={socialIconStyle}
          onMouseEnter={(e) => (e.target.style.color = "#1DA1F2")}
          onMouseLeave={(e) => (e.target.style.color = "#000")}
        />
        <i
          className="fab fa-instagram"
          style={socialIconStyle}
          onMouseEnter={(e) => (e.target.style.color = "#C13584")}
          onMouseLeave={(e) => (e.target.style.color = "#000")}
        />
        <i
          className="fab fa-linkedin"
          style={socialIconStyle}
          onMouseEnter={(e) => (e.target.style.color = "#0077B5")}
          onMouseLeave={(e) => (e.target.style.color = "#000")}
        />
      </p>
    </footer>
  );
};

export default Footer;
