import React, { useState } from "react";

/**
 * Contact component for user inquiries.
 * Provides a form to collect user details and messages.
 *
 * @component
 * @example
 * return (
 *   <Contact />
 * )
 */
const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form Data being sent:", formData); // Log form data to the console
    setIsSubmitting(true);
  
    try {
      // Send form data to the backend API
      const response = await fetch("http://localhost:3001/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
  
      if (response.ok) {
        setSubmitMessage("Thank you for contacting us! We will get back to you soon.");
        setFormData({ name: "", email: "", message: "" });
      } else {
        setSubmitMessage("There was an issue submitting your message. Please try again.");
      }
    } catch (error) {
      console.error("Error submitting contact form:", error);
      setSubmitMessage("An error occurred. Please try again later.");
    } finally {
      setIsSubmitting(false);
    }
  };
  

  const formStyle = {
    maxWidth: "600px",
    margin: "0 auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    backgroundColor: "#fff",
  };

  const inputStyle = {
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    border: "1px solid #ccc",
    borderRadius: "5px",
    fontSize: "14px",
  };

  const buttonStyle = {
    backgroundColor: "#007bff",
    color: "#fff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  };

  buttonStyle.hover = {
    backgroundColor: "#0056b3",
  };

  return (
    <div style={{ padding: "50px 20px", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ textAlign: "center" }}><br />Contact Us</h2>
      <form style={formStyle} onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          style={inputStyle}
          required
        />
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          style={inputStyle}
          required
        />
        <label>Message:</label>
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          style={{ ...inputStyle, height: "100px" }}
          required
        ></textarea>
        <button
          type="submit"
          style={buttonStyle}
          onMouseEnter={(e) => (e.target.style.backgroundColor = buttonStyle.hover.backgroundColor)}
          onMouseLeave={(e) => (e.target.style.backgroundColor = buttonStyle.backgroundColor)}
          disabled={isSubmitting} // Disable the button while submitting
        >
          {isSubmitting ? "Submitting..." : "Submit"}
        </button>
      </form>
      {submitMessage && (
        <p style={{ textAlign: "center", marginTop: "20px", color: isSubmitting ? "orange" : "green" }}>
          {submitMessage}
        </p>
      )}
    </div>
  );
};

export default Contact;
