import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar"; // Import Sidebar component
import axios from "axios";
import "./DataUpload.css";

const DataUpload = () => {
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState(null); // State to hold the summary data
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Define the functionalities you want to display in the sidebar
  const functionalities = [
    "Data Upload",
    "Preview and Profiling",
    "Redundancy and Consistency Cleaning",
    "Outlier Detection",
    "Data Standardization",
    "Data Transformation",
    "Collaborative Cleaning",
    "Export Options",
    "Profile", 
    "Logout"
  ];

  // Handle functionality click and navigate to the respective route
  const handleFunctionalityClick = (name) => {
    console.log("Selected functionality:", name);

    // Mapping functionalities to routes
    const routeMap = {
      "Data Upload": "/dataupload",
      "Preview and Profiling": "/previewandprofiling",
      "Redundancy and Consistency Cleaning": "/redundancy-cleaning",
      "Outlier Detection": "/outlier-detection",
      "Data Standardization": "/data-standardization",
      "Data Transformation": "/data-transformation",
      "Collaborative Cleaning": "/collaborative-cleaning",
      "Export Options": "/export-options",
      "Profile": "/profile",
      "Logout": "/logout",
    };

    // Navigate to the respective route
    if (routeMap[name]) {
      navigate(routeMap[name]);
    }
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!file) return alert("Please select a file.");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:5001/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.status === 200) {
        alert("File uploaded successfully!");
        fetchSummary(response.data.tableName); // Fetch the summary after file upload
      }
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("There was an error uploading the file.");
    }
  };

  // Fetch summary after upload
  const fetchSummary = async (tableName) => {
    try {
      const response = await axios.get("http://localhost:5001/previewandprofiling", {
        params: { table_name: tableName },
      });
      setSummary(response.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "An error occurred");
      setSummary(null);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar for navigation */}
      <Sidebar
        functionalities={functionalities}
        onFunctionalityClick={handleFunctionalityClick} // Pass click handler
      />

      {/* Main content area */}
      <div className="data-upload-container">
        <div className="data-upload">
          <h2>Upload Dataset</h2>
          <div className="file-upload">
            <input type="file" accept=".csv, .xlsx" onChange={handleFileChange} />
          </div>
          <button onClick={handleUpload}>Upload</button>
        </div>

        {/* Displaying the summary */}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {summary && (
          <div style={{ marginTop: "20px" }}>
            <h3>Summary Report for Table: {summary.table_name}</h3>
            <p><strong>Column Names:</strong> {summary.column_names.join(", ")}</p>
            <p><strong>Data Types:</strong> {summary.data_types.join(", ")}</p>
            <p><strong>Missing Values:</strong> {summary.missing_values.join(", ")}</p>
            <p><strong>Duplicates:</strong> {summary.duplicates}</p>
            <h4>Key Statistics:</h4>
            <pre style={{ background: "#f0f0f0", padding: "10px" }}>
              {JSON.stringify(summary.key_statistics, null, 2)}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default DataUpload;
