import React, { useState } from "react";
import "./PreviewAndProfiling.css";

const PreviewAndProfiling = () => {
  const [preview, setPreview] = useState(null);
  const [plot, setPlot] = useState(null);
  const [error, setError] = useState("");

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];

    if (!file) {
      setError("No file selected!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setPreview(data.preview);
        setPlot(data.plot);
        setError("");
      } else {
        const errorData = await response.json();
        setError(errorData.error || "An error occurred!");
      }
    } catch (err) {
      setError("Failed to connect to the server!");
    }
  };

  return (
    <div className="preview-and-profiling">
      <h2>Preview and Profiling</h2>
      <p>Analyze and preview your data structure with visualization.</p>

      <div className="file-upload">
        <input type="file" accept=".csv, .xlsx" onChange={handleFileUpload} />
      </div>

      {error && <p className="error">{error}</p>}

      {preview && (
        <div className="data-preview">
          <h3>Data Preview</h3>
          <table>
            <thead>
              <tr>
                {Object.keys(preview[0]).map((key) => (
                  <th key={key}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {preview.map((row, index) => (
                <tr key={index}>
                  {Object.values(row).map((value, idx) => (
                    <td key={idx}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {plot && (
        <div className="data-visualization">
          <h3>Visualization</h3>
          <img src={plot} alt="Data Visualization" />
        </div>
      )}
    </div>
  );
};

export default PreviewAndProfiling;
