import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

/**
 * Login page component for the web application.
 * Allows users to log in with their email and password.
 *
 * @component
 * @example
 * return (
 *   <Login />
 * )
 */
const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Initialize useNavigate to programmatically navigate to another page

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Check if the credentials match the hardcoded values
    if (email === "test@example.com" && password === "testpassword") {
      alert("Login successful!");
      navigate("/dashboard"); // Redirect to the dashboard page
    } else {
      alert("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h1>Login </h1>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label>Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-btn">Login</button>
        <p>Don't have an account? <a href="/signup">Sign Up</a></p>
      </form>
    </div>
  );
};

export default Login;
