import React, { useState, useEffect } from "react";
import axios from "axios";
import "./RedundancyCleaning.css";

const RedundancyCleaning = () => {
  const [cleanedData, setCleanedData] = useState([]);
  const [missingValueOption, setMissingValueOption] = useState("mean");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch dataset from the backend
  useEffect(() => {
    axios
      .get("http://localhost:3000/data")
      .then((response) => {
        const fetchedData = response.data;
        if (fetchedData?.data) {
          setCleanedData(fetchedData.data); // Initialize cleaned data
        } else {
          setError("No data available");
        }
      })
      .catch((err) => {
        console.error("Error fetching data:", err);
        setError("Failed to fetch data");
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  const handleRemoveDuplicates = () => {
    axios
      .post("http://localhost:3000/clean", { remove_duplicates: true })
      .then((response) => {
        const cleaned = response.data;
        if (cleaned?.data) setCleanedData(cleaned.data);
      })
      .catch((err) => console.error("Error cleaning data:", err));
  };

  const handleMissingValues = () => {
    axios
      .post("http://localhost:3000/clean", {
        missing_value_option: missingValueOption,
        custom_value: "CustomValue", // Optional if custom_value is selected
      })
      .then((response) => {
        const cleaned = response.data;
        if (cleaned?.data) setCleanedData(cleaned.data);
      })
      .catch((err) => console.error("Error handling missing values:", err));
  };

  if (loading) {
    return <div>Loading data...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="redundancy-cleaning">
      <h2>Redundancy and Consistency Cleaning</h2>

      <div className="options">
        <button onClick={handleRemoveDuplicates}>Remove Duplicates</button>
        <div className="missing-value-options">
          <label htmlFor="missing-value-select">Handle Missing Values:</label>
          <select
            id="missing-value-select"
            value={missingValueOption}
            onChange={(e) => setMissingValueOption(e.target.value)}
          >
            <option value="mean">Replace with Mean</option>
            <option value="median">Replace with Median</option>
            <option value="custom">Replace with Custom Value</option>
            <option value="flag">Flag as MISSING</option>
          </select>
          <button onClick={handleMissingValues}>Apply</button>
        </div>
      </div>

      <div className="data-preview">
        <h3>Cleaned Data Preview</h3>
        {cleanedData.length > 0 ? (
          <table>
            <thead>
              <tr>
                {cleanedData[0]?.map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {cleanedData.slice(1).map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex}>{cell || "-"}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No data to display</p>
        )}
      </div>
    </div>
  );
};

export default RedundancyCleaning;
