import React, { useState } from "react";
import axios from "axios";
import "./DataUpload.css";

export const DataUpload = () => {
  const [file, setFile] = useState(null);
  const [previewData, setPreviewData] = useState(null);
  const [summaryReport, setSummaryReport] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file to upload!");
      return;
    }
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setPreviewData(response.data.preview);
      setSummaryReport(response.data.summary);
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload file. Please try again.");
    }
  };

  return (
    <div className="data-upload">
      <h2>Upload and Preview Your Data</h2>
      <input type="file" accept=".csv, .xlsx" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      {previewData && (
        <div className="preview-section">
          <h3>Data Preview</h3>
          <table>
            <thead>
              <tr>
                {Object.keys(previewData[0]).map((key, index) => (
                  <th key={index}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {previewData.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {Object.values(row).map((value, colIndex) => (
                    <td key={colIndex}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {summaryReport && (
        <div className="summary-section">
          <h3>Summary Report</h3>
          <ul>
            <li>Column Names: {summaryReport.column_names.join(", ")}</li>
            <li>Data Types: {summaryReport.data_types.join(", ")}</li>
            <li>Missing Values: {summaryReport.missing_values}</li>
            <li>Duplicates: {summaryReport.duplicates}</li>
            <li>Key Statistics: {summaryReport.key_statistics}</li>
          </ul>
        </div>
      )}
    </div>
  );
};
