// PreviewAndProfiling.jsx
import React, { useState } from "react";
import * as XLSX from "xlsx"; // Install using `npm install xlsx`
import "./PreviewAndProfiling.css";

const PreviewAndProfiling = () => {
  const [data, setData] = useState([]);
  const [summary, setSummary] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (!file) return;

    const reader = new FileReader();

    reader.onload = (e) => {
      const binaryStr = e.target.result;
      const workbook = XLSX.read(binaryStr, { type: "binary" });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      setData(jsonData);
      generateSummary(jsonData);
    };

    reader.readAsBinaryString(file);
  };

  const generateSummary = (data) => {
    if (data.length < 1) return;

    const headers = data[0];
    const summary = headers.map((columnName, index) => {
      const columnData = data.slice(1).map((row) => row[index]);
      const dataType = columnData.every((value) => !isNaN(value)) ? "Number" : "String";
      const missingValues = columnData.filter((value) => value === undefined || value === null || value === "").length;
      const duplicates = new Set(columnData).size !== columnData.length;

      return {
        columnName,
        dataType,
        missingValues,
        duplicates,
      };
    });

    setSummary(summary);
  };

  return (
  <div><br/><br/>
    <div className="preview-and-profiling">
      <h2>Preview and Profiling</h2>
      <p>Analyze and preview your data structure and characteristics here.</p>
      </div>
      <div className="file-upload">
        <input type="file" accept=".csv, .xlsx" onChange={handleFileUpload} />
      </div>

      {data.length > 0 && (
        <div className="data-preview">
          <h3>Data Preview</h3>
          <table>
            <thead>
              <tr>
                {data[0].map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.slice(1).map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex}>{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {summary && (
        <div className="data-summary">
          <h3>Summary Report</h3>
          <table>
            <thead>
              <tr>
                <th>Column Name</th>
                <th>Data Type</th>
                <th>Missing Values</th>
                <th>Duplicates</th>
              </tr>
            </thead>
            <tbody>
              {summary.map((col, index) => (
                <tr key={index}>
                  <td>{col.columnName}</td>
                  <td>{col.dataType}</td>
                  <td>{col.missingValues}</td>
                  <td>{col.duplicates ? "Yes" : "No"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PreviewAndProfiling;
